sys_variable_value.value:
(function execute(inputs, outputs) {
       /**
     * Calculates weekly transportation rate based on distance and sibling discount.
     *
     * Rules:
     * - Base flat rate = $65/week for up to 3 miles
     * - Additional $5 per mile beyond 3 miles
     * - 10% sibling discount applies to additional riders (not the first)
     * - Adds total riders and average weekly rate per rider
     */

    // Parse inputs
    const distance = parseFloat(inputs.distance);
    const siblings = parseInt(inputs.additionalRiders || 0, 10);

    // Validate inputs
    if (isNaN(distance) || distance < 0) {
        outputs.error = "Invalid distance input.";
        return;
    }

    if (isNaN(siblings) || siblings < 0) {
        outputs.error = "Invalid siblings input.";
        return;
    }

    // Constants
    const BASE_RATE = 65;
    const EXTRA_MILE_RATE = 5;
    const SIBLING_DISCOUNT = 0.1; // 10%

    // Compute rate
    const extraMiles = Math.max(0, distance - 3);
    const extraCharge = extraMiles * EXTRA_MILE_RATE;
    const weeklyRate = BASE_RATE + extraCharge;

    // Apply sibling discount (first rider full, others 10% off)
    const siblingRate = weeklyRate * (1 - SIBLING_DISCOUNT);
    const totalCost = weeklyRate + (siblingRate * siblings);

    // New calculations
    const totalRiders = siblings + 1;
    const averageWeeklyRate = totalCost / totalRiders;

    // Set outputs
    outputs.distance = distance.toFixed(2);
    outputs.siblings = siblings;
    outputs.total_riders = totalRiders;
    outputs.weeklyRate = weeklyRate.toFixed(2);
    outputs.totalcost = totalCost.toFixed(2);
    outputs.average_weekly_rate = averageWeeklyRate.toFixed(2);

})(inputs, outputs);

